&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //点击事件&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    click(d)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      if (d.children)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        d._children=d.children;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        d.children = null&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &amp;#125;else&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        d.children = d._children&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if (d.children === [] || d.children == null)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;          return;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        d.children.forEach(this.collapse)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        d._children = null;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      let depths = this.treeDepths(this.treeData);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      this.viewerHeight = depths.length * 100;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      if (this.viewerHeight &amp;lt; this.minHeight)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        this.viewerHeight = this.minHeight;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      this.tree = d3.layout.tree()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;          .size([this.viewerHeight, this.viewerWidth]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      this.update(d)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125;,&lt;/span&gt;&lt;br&gt;&lt;span cla