n&gt; jobExecutionContext.getTrigger();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;TriggerKey&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;triggerKey&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; trigger.getKey();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;triggerKeyName&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; triggerKey.getName();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;triggerKeyGroup&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; triggerKey.getGroup();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;triggerClazzName&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; triggerKey.getClass().getName();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        log.info(&lt;span class=&#34;string&#34;&gt;&amp;quot;触发器的唯一标识名称：&amp;quot;&lt;/span&gt; + triggerKeyName);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        log.info(&lt;span class=&#34;string&#34;&gt;&amp;quot;触发器的组名：&amp;quot;&lt;/span&gt; + triggerKeyGroup);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        log.info(&lt;span class=&#34;string&#34;&gt;&amp;quot;触发器绑定的任务类信息：&amp;quot;&lt;/span&gt; + triggerClazzName);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        log.info(&lt;span class=&#34;string&#34;&gt;&amp;quot;-----------------------------------------&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;comment&#34;&gt;//通过JobExecutionContext&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;